import unittest

from azure.cli.core.credential_helper import CredentialType, is_containing_credential, distinguish_credential, redact_credential_for_string, redact_credential


class TestCredentialHelper(unittest.TestCase):

    def test_redact_credential_for_string(self):
        sas_token = 'sv=2019-02-02&ss=bfqt&srt=sco&sp=rwdlacup&se=2020-02-12T00:00:00Z&st=2020-02-11T00:00:00Z&spr=https&sig=u0JHN%2Bix9jsXw71NwfNF6TtQvckxuHHGRI6ldXzRMDA%3D'
        expected_sas_token = 'sv=2019-02-02&ss=bfqt&srt=sco&sp=rwdlacup&se=2020-02-12T00:00:00Z&st=2020-02-11T00:00:00Z&spr=https&sig=_REDACTED_SAS_TOKEN_SIG_'
        self.assertEqual(redact_credential_for_string(sas_token), expected_sas_token)

        access_token = 'eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsIng1dCI6IlQxU3QtZExUdnlXUmd4Ql82NzZ1OGtyWFMtSSIsImtpZCI6IlQxU3QtZExUdnlXUmd4Ql82NzZ1OGtyWFMtSSJ9..s1FnxnZDAEQaNGZFuNLHHXLfPxzzpERz8nySgZoQVacaCAokBrvs5eiwWyVdhtVJt2OpQAiku7Uul1fXQg_GXUq2mqrZh9Ttn3Cp4gRHSNNJDEYB5VINyk4MxVCqB-Gkir0-qU5DVtViTNJOPw-LssYImOXmxIrqDwLas4Tn1yRS-mD50nzTFeg5gpsSlr0lZWmJQfE6ZtdP6oEEUVs3rg2kNHRbYnrPOjJa3B8DCLxkwyE9Mt6Ti7nUu979Lm0jTEOvHn0bh1f-Y5F9edlNYuK3z5PMn-FYP5ws0IEClC82VpDpNYw6ROkUo6KHVnI37LivuwNDxmDhRwb_2Iy8Ow'
        expected_access_token = '_REDACTED_JWT_TOKEN_'
        self.assertEqual(redact_credential_for_string(access_token), expected_access_token)

        bearer_token = 'bearer eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsIng1dCI6IjVCM25SeHRRN2ppOGVORGMzRnkwNUtmOTdaRSIsImtpZCI6IjVCM25SeHRRN2ppOGVORGMzRnkwNUtmOTdaRSJ9..DdlA4orzkSO-nAjNUb6erVgEMtwbx47fQ2joBnSkcuaGk7QPzJGTY7kweaIMs9uYrO5bvG4aTN_7kaZdbnWA6HWdn6Vb9zz5plINggPiMu08-2tc5Ks1vlNMjQLjrapVgxNOlvTqhMF5ftScbSftb8hwKI_TZYKgLGqMdZzG10dVMiRxXiQL7AqD4TL6fUDaINtZDvlsx8VIrE0aK6tRk_vhrT-Mkc1i09RPASoEBGWzuEk0mYNfX8JSpHsuwT8saCekOv23eyJJB26CHV88oFlipDcg6NOJ89XMJtTg7bbe3FUgoDeaDjCWuSd5_wKCJJRmtuuPoX2Nc5X28I4C9g'
        expected_bearer_token = 'bearer _REDACTED_BEARER_TOKEN_'
        self.assertEqual(redact_credential_for_string(bearer_token), expected_bearer_token)

        ssh_pub_key = 'ssh-rsa  test@example.com'
        expected_ssh_pub_key = 'ssh-rsa _REDACTED_SSH_KEY_ _REDACTED_EMAIL_@example.com'
        self.assertEqual(redact_credential_for_string(ssh_pub_key), expected_ssh_pub_key)

        object_id = '3707fb2f-ac10-4591-a04f-8b0d786ea37d'
        expected_object_id = '_REDACTED_GUID_'
        self.assertEqual(redact_credential_for_string(object_id), expected_object_id)

        cosmosdb_key = 'w1fdSjoE3kxJxTNWsABHuSGyux9jvSVCRibr3bRNXWmyUMu88W6FweIsqXEoy4zl9i6r7764Hg8EACDbQroe2Q=='
